import React, { useState } from 'react';

const mapTemperatureToAngle = (min, max, current) => {
  const angleRange = 270;
  const temperatureRange = max - min;
  const normalizedTemperature = current - min;
  return (normalizedTemperature / temperatureRange) * angleRange - 135;
};

const TemperatureGauge = ({ minTemp, maxTemp }) => {
  const [currentTemp, setCurrentTemp] = useState((minTemp + maxTemp) / 2);

  const angle = mapTemperatureToAngle(minTemp, maxTemp, currentTemp);

  const handleTemperatureChange = (event) => {
    const newTemp = Math.min(maxTemp, Math.max(minTemp, event.target.value));
    setCurrentTemp(newTemp);
  };

  return (
    <div className="flex flex-col items-center p-4 bg-white rounded-lg shadow-md">
      <svg className="w-64 h-32" viewBox="0 0 200 100">
        <circle cx="100" cy="100" r="80" stroke="gray" strokeWidth="2" fill="transparent" />
        <line x1="100" y1="100" x2="100" y2="20" stroke="black" strokeWidth="2"
          transform={`rotate(${angle} 100 100)`} />
        <line x1="20" y1="100" x2="40" y2="100" stroke="red" strokeWidth="2" />
        <line x1="160" y1="100" x2="180" y2="100" stroke="blue" strokeWidth="2" />
      </svg>
      <div className="text-xl font-semibold text-gray-700">{currentTemp}Â°C</div>
      <input 
        className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer dark:bg-gray-700"
        type="range" 
        min={minTemp} 
        max={maxTemp} 
        value={currentTemp} 
        onChange={handleTemperatureChange} 
      />
    </div>
  );
};

export default TemperatureGauge;
