import React, { useState } from 'react';
import '../styles/TemperatureGauge.css'; // Import the styles

// Function to map temperature to gauge angle
const mapTemperatureToAngle = (min, max, current) => {
  const angleRange = 270; // Full range of the gauge
  const temperatureRange = max - min;
  const normalizedTemperature = current - min;
  return (normalizedTemperature / temperatureRange) * angleRange - 135;
};

// TemperatureGauge component
const TemperatureGauge = ({ minTemp, maxTemp }) => {
  const [currentTemp, setCurrentTemp] = useState((minTemp + maxTemp) / 2);
  const angle = mapTemperatureToAngle(minTemp, maxTemp, currentTemp);

  // Handle change in temperature via the slider
  const handleTemperatureChange = (event) => {
    setCurrentTemp(event.target.value);
  };

  return (
    <div className="temperature-gauge-container">
      <svg className="temperature-gauge-svg" viewBox="0 0 200 100">
        {/* Circle for the gauge */}
        <circle cx="100" cy="100" r="80" stroke="gray" strokeWidth="2" fill="transparent" />

        {/* Line indicating the current temperature */}
        <line x1="100" y1="100" x2="100" y2="20" stroke="black" strokeWidth="2"
          transform={`rotate(${angle} 100 100)`} />

        {/* Static lines for min and max temperatures */}
        <line x1="100" y1="5" x2="100" y2="15" stroke="black" strokeWidth="2"
          transform="rotate(-135 100 100)" />
        <line x1="100" y1="5" x2="100" y2="15" stroke="black" strokeWidth="2"
          transform="rotate(135 100 100)" />
      </svg>

      {/* Text displaying the current temperature */}
      <div className="temperature-gauge-text">{currentTemp}Â°C</div>

      <input 
        className="temperature-gauge-input"
        type="range"
        min={minTemp}
        max={maxTemp}
        value={currentTemp}
        onChange={handleTemperatureChange}
      />
    </div>
  );
};

export default TemperatureGauge;
