import React, { useState} from 'react';
import '../styles/TemperatureGauge.css';

const TemperatureGauge = ({ initialMinTemp, initialMaxTemp, initialCurrentTemp }) => {

    const [minTemp, setMinTemp] = useState(initialMinTemp);
    const [maxTemp, setMaxTemp] = useState(initialMaxTemp);
    const [currentTemp, setCurrentTemp] = useState(initialCurrentTemp);

    const getRotationDegrees = (min, max, current) => {
        const range = max - min;
        const relativeValue = current - min;
        return (relativeValue / range) * 180 - 90;
    };

    const needleStyle = {
        transform: `rotate(${getRotationDegrees(minTemp, maxTemp, currentTemp)}deg)`,
        transformOrigin: '50% 100%'
    };

    return (
        <div className='flex flex-col items-center p-4'>
        <svg className='w-48 h-24' viewBox='0 0 36 18'>
  {/* Arc for the gauge */}
  <path d="M 2 18 a 16 16 0 0 1 32 0" fill="none" stroke="black" strokeWidth="2" />
  {/* Needle to indicate current temperature */}
  {/* Adjust the x2 and y2 coordinates to position the tip of the needle appropriately */}
  <line className='stroke-current text-black' x1='18' y1='18' x2='18' y2='12' style={needleStyle} strokeWidth="2" />
  {/* Label for the temperature */}
  {/* You might need to adjust the x and y coordinates as well as font size to position the text appropriately */}
  <text x="18" y="35" textAnchor="middle" fontSize="3">{currentTemp}°C</text>
</svg>
            <div className='text-2xl font-bold'>{currentTemp}°C</div>
            <div className='space-x-2 mt-4'>
                <label className='inline-flex flex-col items-center text-sm'>
                    Min Temp:
                    <input
                        type='number'
                        className='mt-1 px-2 py-1 border rounded'
                        value={minTemp}
                        onChange={(e) => setMinTemp(e.target.value)}
                    />
                </label>
                <label className='inline-flex flex-col items-center text-sm'>
                    Max Temp:
                    <input
                        type='number'
                        className='mt-1 px-2 py-1 border rounded'
                        value={maxTemp}
                        onChange={(e) => setMaxTemp(e.target.value)}
                    />
                </label>
                <label className='inline-flex flex-col items-center text-sm'>
                    Current Temp:
                    <input
                        type='number'
                        className='mt-1 px-2 py-1 border rounded'
                        value={currentTemp}
                        onChange={(e) => setCurrentTemp(e.target.value)}
                    />
                </label>
            </div>
        </div>
    );
};  

export default TemperatureGauge;
