import React from 'react';
import '../styles/TemperatureGauge.css'; // CSS for styling the component

const TemperatureGauge = ({ minTemp, maxTemp, currentTemp }) => {
  // Calculate the needle rotation in degrees
  const getRotationDegrees = (min, max, current) => {
    const range = max - min;
    const relativeValue = current - min;
    return (relativeValue / range) * 180 - 90; // Adjusting so that -90 is min, and 90 is max
  };

  // Inline styles for the needle
  const needleStyle = {
    transform: `rotate(${getRotationDegrees(minTemp, maxTemp, currentTemp)}deg)`,
    transformOrigin: '50% 100%' // Pivot at the bottom center of the needle
  };

  return (
    <div className='thermostat-display'>
      <svg className='thermostat-svg' viewBox='0 0 36 18'>
        {/* Outer circle for the thermostat */}
        <circle cx='18' cy='18' r='17.5' fill='none' stroke='#333' strokeWidth='1' />
        {/* Lines for min and max temperature */}
        <line x1='1' y1='18' x2='4' y2='18' stroke='#333' strokeWidth='0.5' />
        <line x1='32' y1='18' x2='35' y2='18' stroke='#333' strokeWidth='0.5' />
        {/* Needle to indicate current temperature */}
        <line className='needle' x1='18' y1='1' x2='18' y2='18' style={needleStyle} />
      </svg>
      <div className='temperature-display'>{currentTemp}Â°C</div>
    </div>
  );
};

export default TemperatureGauge;
