import React, { useState } from 'react';
import './App.css';
import TemperatureGauge from './components/TemperatureGauge';

const App = () => {
  // State for the temperatures
  const [minTemp, setMinTemp] = useState(0);
  const [maxTemp, setMaxTemp] = useState(100);
  const [currentTemp, setCurrentTemp] = useState(20);

  // Handlers to change the temperatures
  const handleMinTempChange = (e) => setMinTemp(e.target.value);
  const handleMaxTempChange = (e) => setMaxTemp(e.target.value);
  const handleCurrentTempChange = (e) => setCurrentTemp(e.target.value);

  return (
    <div className='app'>
      <ThermostatDisplay minTemp={minTemp} maxTemp={maxTemp} currentTemp={currentTemp} />
      <div className='controls'>
        <label>
          Min Temp:
          <input type='number' value={minTemp} onChange={handleMinTempChange} />
        </label>
        <label>
          Max Temp:
          <input type='number' value={maxTemp} onChange={handleMaxTempChange} />
        </label>
        <label>
          Current Temp:
          <input type='number' value={currentTemp} onChange={handleCurrentTempChange} />
        </label>
      </div>
    </div>
  );
};

export default App;
